mapper.insert:
insert into schedule_unit(
	schedule_unit_no, schedule_no, schedule_unit_content, schedule_unit_time,
	schedule_unit_lat, schedule_unit_lng, schedule_unit_position,
	schedule_unit_day, schedule_unit_name, schedule_key
)
values(
	#{scheduleUnitNo}, #{scheduleNo}, #{scheduleUnitContent}, #{scheduleUnitTime},
	#{scheduleUnitLat}, #{scheduleUnitLng}, #{scheduleUnitPosition},
	#{scheduleUnitDay}, #{scheduleUnitName}, #{scheduleKey}
)
